import React, { useContext,useState } from "react";
import Layout from "../components/base/Layout";
import { FormControl, IconButton, Input, InputAdornment, InputLabel, TextField } from "@material-ui/core";
import styles from "../assets/scss/base/login.module.scss";
import "../assets/scss/base/signUp.scss";
import { StepContext } from "../hooks/StepContext";
import { Visibility, VisibilityOff } from "@material-ui/icons";

const SignIn = ()=>{
  interface State {
    password: string;
    showPassword: boolean;
  }
  const [values, setValues] = useState<State>({
    password: "",
    showPassword: false,
  });

  const step = useContext(StepContext);
  const renderForm = ()=>{
    const handleChange = (prop: keyof State) => (event: React.ChangeEvent<HTMLInputElement>) => {
      setValues({ ...values, [prop]: event.target.value });
    };

    const handleClickShowPassword = () => {
      setValues({ ...values, showPassword: !values.showPassword });
    };

    const handleMouseDownPassword = (event: React.MouseEvent<HTMLButtonElement>) => {
      event.preventDefault();
    };
    return(
      <FormControl className={styles.textField}>
        <InputLabel htmlFor="standard-adornment-password">Password</InputLabel>
        <Input
          id="standard-adornment-password"
          type={values.showPassword ? "text" : "password"}
          value={values.password}
          onChange={handleChange("password")}
          endAdornment={
            <InputAdornment position="end">
              <IconButton
                aria-label="toggle password visibility"
                onClick={handleClickShowPassword}
                onMouseDown={handleMouseDownPassword}
              >
                {values.showPassword ? <Visibility /> : <VisibilityOff />}
              </IconButton>
            </InputAdornment>
          }
        />
      </FormControl>
    );
  };
  return(
    <Layout>
      <div className={styles.main}>
        <div className={styles.contentContainer}>
          <div className={styles.header}>
            <span>Welcome to BHyve</span>
          </div>
          <div>
            {renderForm()}
          </div>
          <div className={styles.button}>
            <span>Sign In</span>
          </div>
          <div className={styles.sHeader}>
            <span>Not registered yet?</span>
          </div>
          <div onClick={()=>step[1](1)}  className={styles.button}>
            <span>Register</span>
          </div>
        </div>
      </div>
    </Layout>
  );
};

export default SignIn;
