import React, { useContext, useEffect } from "react";
import { SkillsContext } from "../../hooks/SkillsContext";
import styles from "../../assets/scss/base/skillsSelector.module.scss";
import { Chip, Paper } from "@material-ui/core";

interface ChipData {
    key: number;
    label: string;
}
const SkillsSelector = ()=>{
  const skills = useContext(SkillsContext);
  const handleDelete = (chipToDelete: ChipData ) => () => {
    skills[1]((chips) => chips.filter((chip) => chip.key !== chipToDelete.key));
  };

  useEffect(()=>{
    let skillsData:any = [{ key: 0, label: "Angular" },
      { key: 1, label: "jQuery" },
      { key: 2, label: "Polymer" },
      { key: 3, label: "React" },
      { key: 4, label: "Vue.js" }];
    skills[1](skillsData);
  });

  const renderSelectedSkills = ()=>{
    return(
      <Paper component="ul" className={styles.root}>
        {skills[0].map((data) => {
          return (
            <li key={data.key}>
              <Chip
                label={data.label}
                onDelete={data.label === "React" ? undefined : handleDelete(data)}
              />
            </li>
          );
        })}
      </Paper>
    );
  };
  return(
    <div>{renderSelectedSkills}</div>
  );
};
export default SkillsSelector;
